
  $(document).ready(function () {
    // Hero Slider (already working)
    $(".hero-slider").owlCarousel({
      items: 1,
      loop: true,
      autoplay: true,
      autoplayTimeout: 5000,
      dots: true,
      nav: false
    });

    // Testimonial Carousel with buttons
    $(".testimonial-carousel").owlCarousel({
      autoplay: true,
      smartSpeed: 800,
      center: true,
      dots: true,
      nav: true, // âœ… enable buttons
      loop: true,
      margin: 25,
      navText: [
        '<i class="bi bi-arrow-left-circle"></i>',
        '<i class="bi bi-arrow-right-circle"></i>'
      ],
      responsive: {
        0: { items: 1 },
        768: { items: 2 },
        992: { items: 3 }
      }
    });
  });